// Filename - components/Home.js

import React, {useState, useEffect} from "react";
import { Button, Table } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import array from "./array";
import { Link, useNavigate } from "react-router-dom";
import CustomPagination from "../Utils/CustomPagination"

function Home() {
	let history = useNavigate();
	const [totalData, setTotalData] = useState(array);
	const totalDataLength = totalData?.length;
	const [currentPage, setCurrentPage] = useState("1");
	const itemPerPage = 1;

	const paginationClicked = (event) => {
		var itemClicked = event.target.text;
		setCurrentPage(itemClicked)
	}

	// You may skip this part if you're
	// using react-context api or redux
	function setID(id, name, age, description) {
		localStorage.setItem("id", id);
		localStorage.setItem("Name", name);
        localStorage.setItem("Description", description);
		localStorage.setItem("Age", age);
	}

	function getPaginatedData(array, offset, limit) {
		if(limit < 0) return array.slice(offset);
		return array.slice(offset, offset + limit);
	}

	useEffect(()=> {
		const abc = getPaginatedData(totalData, startIndex, itemPerPage)
		setTotalData(totalData)
	}, [currentPage])


	// Deleted function - functionality
	// for deleting the entry
	function deleted(id) {
		let index = array
			.map(function (e) {
				return e.id;
			})
			.indexOf(id);

		// deleting the entry with index
		array.splice(index, 1);

		// We need to re-render the page for getting
		// the results so redirect to same page.
		history("/");
	}

	return (
		<div style={{ margin: "5rem" }}>
			<Table striped bordered hover size="sm">
				<thead>
					<tr>
						<th>Name</th>
                        <th>Description</th>
						<th>Age</th>
						<th>Actions</th>
					</tr>
				</thead>
				<tbody>
					{/* Mapping though every element 
						in the array and showing the 
						data in the form of table */}
					{totalData.map((item) => {
						return (
							<tr>
								<td>{item.Name}</td>
                                <td>{item.Description}</td>
								<td>{item.Age}</td>

								{/* getting theid,name, and 
									age for storing these
									value in the jsx with 
									onclick event */}
								<td>
									<Link to={`/edit`}>
										<Button
											onClick={(e) =>
												setID(
													item.id,
													item.Name,
													item.Age,
                                                    item.Description,
												)
											}
											variant="info"
										>
											Update
										</Button>
									</Link>

								{/* Using thr deleted function passing
									the id of an entry */}
									<Button
										onClick={(e) =>
											deleted(item.id)
										}
										variant="danger"
									>
										Delete
									</Button>
								</td>
							</tr>
						);
					})}
				</tbody>
			</Table>

			{/* Button for redirecting to create page for
				insertion of values */}
			<Link className="d-grid gap-2" to="/create">
				<Button variant="warning" size="lg">
					Create
				</Button>
			</Link>
			< CustomPagination 
				totalDataLength = {totalDataLength}
				currentPage = {currentPage}
				itemPerPage = {itemPerPage}
				paginationClicked = {paginationClicked}
			/>
		</div>
	);
}

export default Home;
