import React, { useState } from "react";
import { Button, Form, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import array from "./array";
import { v4 as uuid } from "uuid";
import { useNavigate } from "react-router-dom";
import CommonForm from "./CommonForm";

function Create() {
	// Making usestate for setting and
	// fetching a value in jsx
	// const [name, setName] = useState("");
	// const [age, setAge] = useState("");
    // const [description, setDescription] = useState("")

	// Using useNavigation for redirecting to pages
	let history = useNavigate();

	// Function for creating a post/entry
	const handelSubmit = (e) => {
		e.preventDefault(); // Prevent reload

		// const ids = uuid(); // Creating unique id
		// let uniqueId = ids.slice(0, 8); // Slicing unique id

		// // Fetching a value from usestate and
		// // pushing to javascript object
		// let a = name,
		// 	b = age,
        //     c = description;
		// if (name == "" || age == "" || description == "") {
		// 	alert("invalid input");
		// 	return;
		// }
		// array.push({ id: uniqueId, Name: a, Description: c, Age: b});

		// // Redirecting to home page after creation done
		// history("/");
	};



    const [formData, setFormData] = useState({
        name: '',
        age: '',
        description: ''
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        const newErrors = validateForm(formData);
        setErrors(newErrors);

        if (Object.keys(newErrors).length === 0) {
            // Form submission logic here

            const ids = uuid(); // Creating unique id
            let uniqueId = ids.slice(0, 8); // Slicing unique id

            // Fetching a value from usestate and
            // pushing to javascript object
            // let a = ,
            //     b = ,
            //     c = ;
            array.push({ id: uniqueId, Name: formData.name, Description: formData.description, Age: formData.age});

            // Redirecting to home page after creation done
            history("/");

            console.log('Form submitted successfully!');
        } else {
            console.log(`Form submission failed
             due to validation errors.`);
        }
    };

    const validateForm = (data) => {
        console.log('bhargavhere11', data.name)
        const errors = {};

        if (data?.name && !data.name.trim()) {
            errors.name = 'Name is required';
        } else {
            errors.name = ''
        }

        if (data?.description && !data?.description.trim()) {
            errors.name = 'Description is required';
        }

        if (!data.age.trim()) {
            errors.name = 'Age is required';
        }

        // if (!data.email.trim()) {
        //     errors.email = 'Email is required';
        // } else if (!/\S+@\S+\.\S+/.test(data.email)) {
        //     errors.email = 'Email is invalid';
        // }

        // if (!data.password) {
        //     errors.password = 'Password is required';
        // } else if (data.password.length < 8) {
        //     errors.password = `Password must be at 
        //     least 8 characters long`;
        // }

        // if (data.confirmPassword !== data.password) {
        //     errors.confirmPassword = 'Passwords do not match';
        // }

        return errors;
    };


    const [errors, setErrors] = useState({});

    const handleChange = (e) => {
       let newData = []

        // console.log('bhargavhere', e.target);
        const { name, value } = e.target;
        newData[name]= value
        validateForm(newData)
        setFormData({
            ...formData,
            [name]: value,
        });
    };




	return (
        <Container>
            <CommonForm 
                handleSubmit={handleSubmit}
                errors={errors}
                handleChange={handleChange}
                formData={formData}
            />
        </Container>
	);
}

export default Create;
