import React from "react";
import { Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";

function CommonForm({handelSubmit, setName, setAge, setDescription}){


    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
    });


    const validateForm = (data) => {
        const errors = {};

        if (!data.username.trim()) {
            errors.username = 'Username is required';
        }

        if (!data.email.trim()) {
            errors.email = 'Email is required';
        } else if (!/\S+@\S+\.\S+/.test(data.email)) {
            errors.email = 'Email is invalid';
        }

        if (!data.password) {
            errors.password = 'Password is required';
        } else if (data.password.length < 8) {
            errors.password = `Password must be at 
            least 8 characters long`;
        }

        if (data.confirmPassword !== data.password) {
            errors.confirmPassword = 'Passwords do not match';
        }

        return errors;
    };


    const [errors, setErrors] = useState({});

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };


    return(
        <Form className="d-grid gap-2" style={{ margin: "5rem" }}>
            {/* Fetching a value from input textfield in a setName using usestate*/}
            <Form.Group className="mb-3" controlId="formBasicName">
                <Form.Control
                    onChange={(e) =>
                        setName(e.target.value)
                    }
                    type="text"
                    placeholder="Enter Name"
                    required
                />
            </Form.Group>
            {errors.username &&
                <span className="error-message">
                    {errors.username}
                </span>
            }
            <Form.Group className="mb-3" controlId="formBasicDescription">
                <Form.Control
                    onChange={(e) =>
                        setDescription(e.target.value)
                    }
                    type="textarea"
                    placeholder="Enter Description"
                    required
                />
            </Form.Group>

            {/* Fetching a value from input textfirld in a setAge using usestate*/}
            <Form.Group
                className="mb-3"
                controlId="formBasicAge"
            >
                <Form.Control
                    onChange={(e) =>
                        setAge(e.target.value)
                    }
                    type="number"
                    placeholder="Age"
                    required
                />
            </Form.Group>

            {/* handing a onclick event in button for firing a function */}
            <Button
                onClick={(e) => handelSubmit(e)}
                variant="primary"
                type="submit"
            >
                Submit
            </Button>
            {/* Redirecting back to home page */}
            <Link className="d-grid gap-2" to="/">
                <Button variant="info" size="lg">
                    Home
                </Button>
            </Link>
        </Form>
    );
}

export default CommonForm;
