// Filename - Edit.js
import React, { useEffect, useState } from "react";
import { Button, Form, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import array from "./array";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";


function Edit() {
	// Here usestate has been used in order
	// to set and get values from the jsx
	const [name, setName] = useState("");
    const [description, setDescription] = useState("");
	const [age, setAge] = useState("");
	const [id, setId] = useState("");
	// Used for navigation with logic in javascript
	let history = useNavigate();

	// Getting an index of an entry with an id
	let index = array
		.map(function (e) {
			return e.id;
		})
		.indexOf(id);

        // Function for handling the edit and
	// pushing changes of editing/updating
	const handelSubmit = (e) => {
		// Preventing from reload
		e.preventDefault();
		if (name == "" || age == "") {
			alert("invalid input");
			return;
		}

		// Getting an index of an array
		let a = array[index];
		// Putting the value from the input
		// textfield and replacing it from
		// existing for updation
		a.Name = name;
		a.Age = age;
        a.Description = description;


		// Redirecting to main page
		history("/");
	};

	// Useeffect take care that page will
	// be rendered only once
	useEffect(() => {
		setName(localStorage.getItem("Name"));
        setDescription(localStorage.getItem("Description"));
		setAge(localStorage.getItem("Age"));
		setId(localStorage.getItem("id"));
	}, []);

	return (
<>
		<Container>
            <CommonForm 
                type={"edit"}
                handleSubmit={handleSubmit}
                errors={errors}
                handleChange={handleChange}
                formData={formData}
            />
        </Container>

		<div>
			<Form className="d-grid gap-2" style={{ margin: "5rem" }}>
				{/* setting a name from the input textfiled */}
				<Form.Group className="mb-3" controlId="formBasicName">
					<Form.Control
						value={name}
						onChange={(e) =>
							setName(e.target.value)
						}
						type="text"
						placeholder="Enter Name"
					/>
				</Form.Group>
                <Form.Group className="mb-3" controlId="formBasicDescription">
					<Form.Control
						value={description}
						onChange={(e) =>
							setDescription(e.target.value)
						}
						type="textarea"
						placeholder="Enter Description"
					/>
				</Form.Group>
				{/* setting a age from the input textfiled */}
				<Form.Group
					className="mb-3"
					controlId="formBasicPassword"
				>
					<Form.Control
						value={age}
						onChange={(e) =>
							setAge(e.target.value)
						}
						type="number"
						placeholder="Age"
					/>
				</Form.Group>

				{/* Hadinling an onclick event 
					running an edit logic */}
				<Button
					onClick={(e) => handelSubmit(e)}
					variant="primary"
					type="submit"
					size="lg"
				>
					Update
				</Button>

				{/* Redirecting to main page after editing */}
				<Link className="d-grid gap-2" to="/">
					<Button variant="warning" size="lg">
						Home
					</Button>
				</Link>
			</Form>
		</div>
		</>
	);
}

export default Edit;
