// ImageGallery.js
import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './ImageGallery.css'; // Import the CSS file for styling

const ImageGallery = () => {
  const [images, setImages] = useState(() => {
    const storedImages = localStorage.getItem('imageOrder');
    return storedImages ? JSON.parse(storedImages) : [
      { id: '1', src: 'images/dev1.png', alt: 'Image 1' },
      { id: '2', src: 'images/dev2.png', alt: 'Image 2' },
      { id: '3', src: 'image3.jpg', alt: 'Image 3' },
      { id: '4', src: 'image3.jpg', alt: 'Image 4' },
      { id: '5', src: 'image3.jpg', alt: 'Image 5' },
      { id: '6', src: 'image3.jpg', alt: 'Image 6' },
      { id: '7', src: 'image3.jpg', alt: 'Image 7' },
      { id: '8', src: 'image3.jpg', alt: 'Image 8' },
      // Add more images as needed
    ];
  });

  useEffect(() => {
    // localStorage.setItem('imageOrder', JSON.stringify(images));
  }, [images]);

  const onDragEnd = (result) => {
    if (!result.destination) return; // Dragged outside the list

    const updatedImages = Array.from(images);
    const [reorderedImage] = updatedImages.splice(result.source.index, 1);
    updatedImages.splice(result.destination.index, 0, reorderedImage);

    // setImages(updatedImages);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <Droppable droppableId="image-gallery">
        {(provided) => (
          <ul {...provided.droppableProps} ref={provided.innerRef} className="image-gallery">
            {images.map((image, index) => (
              <Draggable key={image.id} draggableId={image.id} index={index}>
                {(provided) => (
                  <li
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    ref={provided.innerRef}
                    className="gallery-item"
                  >
                    <img src={image.src} alt={image.alt} className="gallery-image" />
                  </li>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </ul>
        )}
      </Droppable>
    </DragDropContext>
  );
};

export default ImageGallery;
