// ImageGallery.js
import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './ImageGallery.css';
import imageData from './imageData.json';

const ImageGallery = () => {
  const initialImages = [
    { id: '1', src: 'images/image1.jpg', alt: 'Image 1', category: 'Nature' },
    { id: '2', src: 'images/image2.jpg', alt: 'Image 2', category: 'City' },
    { id: '3', src: 'images/image3.jpg', alt: 'Image 3', category: 'Nature' },
    // Add more images as needed with different categories
  ];

  const [images, setImages] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const storedImages = localStorage.getItem('imageOrder');
    const storedCategory = localStorage.getItem('selectedCategory');
    if (storedImages) {
      setImages(JSON.parse(storedImages));
    } else {
      setImages(imageData);
    }

    if (storedCategory) {
      setSelectedCategory(storedCategory);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('imageOrder', JSON.stringify(images));
    localStorage.setItem('selectedCategory', selectedCategory);
  }, [images, selectedCategory]);

  const onDragEnd = (result) => {
    if (!result.destination) return; // Dragged outside the list

    const updatedImages = Array.from(images);
    const [reorderedImage] = updatedImages.splice(result.source.index, 1);
    updatedImages.splice(result.destination.index, 0, reorderedImage);

    setImages(updatedImages);
  };

  const resetOrder = () => {
    setImages(initialImages);
    setSelectedCategory('All');
  };

  const categories = ['All', 'Nature', 'City']; // Add more categories as needed

  const filterImages = (category) => {
    if (category === 'All') {
      setImages(initialImages);
    } else {
      const filteredImages = initialImages.filter((image) => image.category === category);
      // Sort the filtered images by their original order
      filteredImages.sort((a, b) => {
        return initialImages.findIndex((img) => img.id === a.id) - initialImages.findIndex((img) => img.id === b.id);
      });
      setImages(filteredImages);
    }
    setSelectedCategory(category);
  };

  return (
    <div>
      <div className="button-container">
        {categories.map((category) => (
          <button
            key={category}
            className={`category-button ${selectedCategory === category ? 'active' : ''}`}
            onClick={() => filterImages(category)}
          >
            {category}
          </button>
        ))}
        <button className="reset-button" onClick={resetOrder}>
          Reset Sorting & Category Filter
        </button>
      </div>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="image-gallery" direction="horizontal">
          {(provided) => (
            <div>
              <ul {...provided.droppableProps} ref={provided.innerRef} className="image-gallery">
                {images.map((image, index) => (
                  <Draggable key={image.id} draggableId={image.id} index={index}>
                    {(provided, snapshot) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className={`gallery-item ${snapshot.isDragging ? 'gallery-item-dragging' : ''}`}
                      >
                        <img src={image.src} alt={image.alt} className="gallery-image" />
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default ImageGallery;
