import React from "react";
import { Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";

function CommonForm({type, handleSubmit, errors, formData}){

    if(type== 'edit'){

    }

    const validateForm = (data) => {
        const errors = {}; 
        if (!data.name.trim()){
            errors.name = 'Name is required';
        }
        if (!data.description.trim()) {
            errors.description = 'Description is required';
        }
        if (!data.age.trim()) {
            errors.age = 'Age is required';
        }
        return errors;
    };

    const handleChange = (e) => {
        //    let newData = []
            const { name, value } = e.target;
            // newData[name]= value
            // validateForm(newData)
            setFormData({
                ...formData,
                [name]: value,
            });
        };

    return(
        <Form className="d-grid gap-2" style={{ margin: "5rem" }}>
            {/* Fetching a value from input textfield in a setName using usestate*/}
            <Form.Group className="mb-3" controlId="formBasicName">
                <Form.Control
                    onChange={handleChange}
                    type="text"
                    name="name"
                    value={formData.name}
                    placeholder="Enter Name"
                    isValid={formData.name}
                    isInvalid={errors.name}
                    required
                />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicDescription">
                <Form.Control
                    onChange={handleChange}
                    type="textarea"
                    name="description"
                    value={formData.description}
                    placeholder="Enter Description"
                    isValid={formData.description}
                    isInvalid={errors.description}
                    required
                />
            </Form.Group>
            {/* Fetching a value from input textfirld in a setAge using usestate*/}
            <Form.Group className="mb-3" controlId="formBasicAge">
                <Form.Control
                    onChange={handleChange}
                    type="number"
                    name="age"
                    value={formData.age}
                    placeholder="Age"
                    isValid={formData.age}
                    isInvalid={errors.age}
                    required
                />
            </Form.Group>
            {/* handing a onclick event in button for firing a function */}
            <Button onClick={(e) => handleSubmit(e)} variant="primary" type="submit">
                Submit
            </Button>
            {/* Redirecting back to home page */}
            <Link className="d-grid gap-2" to="/">
                <Button variant="info" size="lg">
                    Home
                </Button>
            </Link>
        </Form>
    );
}

export default CommonForm;
