import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './ImageGallery.css';
import imageData from './ImageData.json';

const ImageGallery = () => {
  const [images, setImages] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const categories = ['All', 'Brand', 'Dev'];

  useEffect(() => {
    const storedImages = localStorage.getItem('imageOrder');
    const storedCategory = localStorage.getItem('selectedCategory');

    // images store in state if found from state else default.
    if (storedImages) {
      setImages(JSON.parse(storedImages));
    } else {
      setImages(imageData);
    }

    // localstorage category store in state if found
    if (storedCategory) {
      setSelectedCategory(storedCategory);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('imageOrder', JSON.stringify(images));
    localStorage.setItem('selectedCategory', selectedCategory);
  }, [images, selectedCategory]);

  const onDragEnd = (result) => {
    if (!result.destination) return; // Dragged outside the list error fallback
    const updatedImages = Array.from(images);
    const [reorderedImage] = updatedImages.splice(result.source.index, 1);
    updatedImages.splice(result.destination.index, 0, reorderedImage);
    setImages(updatedImages);
  };

  // reset default value of image and category
  const resetOrder = () => {
    setImages(imageData);
    setSelectedCategory('All');
  };

  const filterImages = (category) => {
    if (category === 'All') {
      setImages(imageData);
    } else {
      const filteredImages = imageData.filter((image) => image.category === category);
      setImages(filteredImages);
    }
    setSelectedCategory(category);
  };

  return (
    <div>
      <div className="button-container">
        {categories.map((category) => (
          <button key={category} className={`button category-button ${selectedCategory === category ? 'active' : ''}`} onClick={() => filterImages(category)} >
            {category}
          </button>
        ))}
        <button className="reset-button" onClick={resetOrder}>
          Reset All
        </button>
      </div>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="image-gallery" direction="horizontal">
          {(dragObject) => (
            <div>
              {console.log('bhargav dragObject', dragObject)}
              <ul {...dragObject.droppableProps} ref={dragObject.innerRef} className="image-gallery-ul">
                {images.map((image, index) => (
                  <Draggable key={image.id} draggableId={image.id} index={index}>
                    {(dragObject, snapshot) => (
                      <div ref={dragObject.innerRef} {...dragObject.draggableProps} {...dragObject.dragHandleProps} className="gallery-item">
                        <img src={image.src} alt={image.alt} className="gallery-image" />
                      </div>
                    )}
                  </Draggable>
                ))}
                {dragObject.placeholder}
              </ul>
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default ImageGallery;
