// Filename - components/Create.js

import React, { useState } from "react";
import { Button, Form, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import array from "./array";
import { v4 as uuid } from "uuid";
import { Link, useNavigate } from "react-router-dom";

function Create() {
	// Making usestate for setting and
	// fetching a value in jsx
	const [name, setName] = useState("");
	const [age, setAge] = useState("");
    const [description, setDescription] = useState("")

	// Using useNavigation for redirecting to pages
	let history = useNavigate();

	// Function for creating a post/entry
	const handelSubmit = (e) => {
		e.preventDefault(); // Prevent reload

		const ids = uuid(); // Creating unique id
		let uni = ids.slice(0, 8); // Slicing unique id

		// Fetching a value from usestate and
		// pushing to javascript object
		let a = name,
			b = age;
            c = description;
		if (name === "" || age == "" || description == "") {
			alert("invalid input");
			return;
		}
		array.push({ id: uni, Name: a, Age: b });

		// Redirecting to home page after creation done
		history("/");
	};

	return (
        <Container>
            <Form className="d-grid gap-2" style={{ margin: "5rem" }}>
                {/* Fetching a value from input textfield in a setName using usestate*/}
                <Form.Group className="mb-3" controlId="formBasicName">
                    <Form.Control
                        onChange={(e) =>
                            setName(e.target.value)
                        }
                        type="text"
                        placeholder="Enter Name"
                        required
                    />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicDescription">
                    <Form.Control
                        onChange={(e) =>
                            setDescription(e.target.value)
                        }
                        type="textarea"
                        placeholder="Enter Description"
                        required
                    />
                </Form.Group>

                {/* Fetching a value from input textfirld in a setAge using usestate*/}
                <Form.Group
                    className="mb-3"
                    controlId="formBasicAge"
                >
                    <Form.Control
                        onChange={(e) =>
                            setAge(e.target.value)
                        }
                        type="number"
                        placeholder="Age"
                        required
                    />
                </Form.Group>

                {/* handing a onclick event in button for firing a function */}
                <Button
                    onClick={(e) => handelSubmit(e)}
                    variant="primary"
                    type="submit"
                >
                    Submit
                </Button>
                {/* Redirecting back to home page */}
                <Link className="d-grid gap-2" to="/">
                    <Button variant="info" size="lg">
                        Home
                    </Button>
                </Link>
            </Form>
        </Container>
	);
}

export default Create;
