// Filename - components/Home.js

import React, {useState, useEffect} from "react";
import { Button, Table, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import array from "./array";
import { Link, useNavigate } from "react-router-dom";
import CustomPagination from "../Utils/CustomPagination";
import { getRestaurantData, deleteRestaurantData } from "./service";

export const List = () => {
	let history = useNavigate();
	const [totalData, setTotalData] = useState([]);
	const [currentPageData, setCurrentPageData] = useState([]);

	const totalDataLength = totalData?.length;


	const [currentPage, setCurrentPage] = useState("1");
	const itemPerPage = 10;
	// let currentPageData = [];

	const paginationClicked = (event) => {
		var itemClicked = event.target.text;
		setCurrentPage(itemClicked)

		const startIndex = (currentPage - 1) * itemPerPage;
		const pageRecords = getPaginatedData(totalData, startIndex, itemPerPage)
		setCurrentPageData(pageRecords)
		currentPageData = pageRecords;
		// setTotalData(pageRecords)
	}

	// You may skip this part if you're

	function setID(id, name, description, address, city) {
		localStorage.setItem("id", id);
		localStorage.setItem("name", name);
        localStorage.setItem("description", description);
		localStorage.setItem("address", address);
		localStorage.setItem("city", city);

	}

	function getPaginatedData(data, offset, limit) {
		if(limit < 0) return data.slice(offset);
		return data.slice(offset, offset + limit);
	}

	useEffect(()=> {
		getRestaurantData()
			.then((response) => {
				setTotalData(response)
			})
			.catch((error) => {
				console.log('API Error', error)
			})

		const startIndex = (currentPage - 1) * itemPerPage;
		const pageRecords = getPaginatedData(totalData, startIndex, itemPerPage)
		currentPageData = pageRecords;
		setTotalData(pageRecords)
	}, [])


	// useEffect(()=> {
	// 	const startIndex = (currentPage - 1) * itemPerPage;
	// 	const pageRecords = getPaginatedData(totalData, startIndex, itemPerPage)
	// 	setTotalData(pageRecords)
	// }, [currentPage])


	// Deleted function - functionality
	// for deleting the entry
	function deleted(id) {


		deleteRestaurantData(id)
			.then((response) => {
				// success message need to show case
				console.log('success', response)
			})
			.catch((error) => {
				console.log('API Error', error)
			})

		let index = totalData
			.map(function (e) {
				return e.id;
			})
			.indexOf(id);

		// deleting the entry with index
		totalData.splice(index, 1);

		// We need to re-render the page for getting
		// the results so redirect to same page.
		history("/");
	}

	return (
		<Container>
			<div className="row text-end">
				<div className="mb-4 mt-4">
					<Link to="/create">
						<Button variant="warning" size="lg">
							Add Restaurant
						</Button>
					</Link>
				</div>
			</div>
			<div className="row">
				<div className="col-12 mb-4">
					<Table striped bordered hover responsive size="sm">
						<thead>
							<tr>
								<th>Name</th>
								<th>Description</th>
								<th>Address</th>
								<th>City</th>
								{/* <th>Created at</th> */}
								<th>Actions</th>
							</tr>
						</thead>
						<tbody>
							{/* Mapping though every element 
								in the array and showing the 
								data in the form of table */}
							{currentPageData.map((item) => {
								return (
									<tr>
										<td>{item.name}</td>
										<td>{item.description}</td>
										<td>{item.address}</td>
										<td>{item.city}</td>
										{/* <td>{item.createdAt}</td> */}
										{/* getting theid,name, and 
											address for storing these
											value in the jsx with 
											onclick event */}
										<td>
											<Link to={`/edit`} className="me-4">
												<Button
													onClick={(e) =>
														setID(
															item.id,
															item.name,
															item.description,
															item.address,
															item.city,
														)
													}
													variant="info"
												>
													Update
												</Button>
											</Link>

										{/* Using thr deleted function passing
											the id of an entry */}
											<Button
												onClick={(e) =>
													deleted(item.id)
												}
												variant="danger"
											>
												Delete
											</Button>
										</td>
									</tr>
								);
							})}
						</tbody>
					</Table>
				</div>
			</div>
			{/* Button for redirecting to create page for
				insertion of values */}
			<div className="row float-end">
				< CustomPagination 
					totalDataLength = {totalDataLength}
					currentPage = {currentPage}
					itemPerPage = {itemPerPage}
					paginationClicked = {paginationClicked}
				/>
			</div>


		</Container>
	);
}
