// Filename - Edit.js
import React, { useEffect, useState } from "react";
import { Button, Form, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import array from "./array";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import CommonForm from "./CommonForm";
import { v4 as uuid } from "uuid";


function Edit() {
	let index = array.map(function (e) {return e.id;}).indexOf(id);
console.log('bhargavhere', index)
    const [errors, setErrors] = useState({});

	const [formData, setFormData] = useState({
        name: '',
        age: '',
        description: ''
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        const newErrors = validateForm(formData);
        setErrors(newErrors);

        if (Object.keys(newErrors).length === 0) {
            // Form submission logic here

            const ids = uuid(); // Creating unique id
            let uniqueId = ids.slice(0, 8); // Slicing unique id

            // Fetching a value from usestate and
            // pushing to javascript object
            // let a = ,
            //     b = ,
            //     c = ;
            array.push({ id: uniqueId, Name: formData.name, Description: formData.description, Age: formData.age});

            // Redirecting to home page after creation done
            history("/");

            console.log('Form submitted successfully!');
        } else {
            console.log(`Form submission failed
             due to validation errors.`);
        }
    };

    const validateForm = (data) => {
        const errors = {}; 

        if (!data.name.trim()){
            errors.name = 'Name is required';
        }

        if (!data.description.trim()) {
            errors.description = 'Description is required';
        }

        if (!data.age.trim()) {
            errors.age = 'Age is required';
        }

        // if (!data.email.trim()) {
        //     errors.email = 'Email is required';
        // } else if (!/\S+@\S+\.\S+/.test(data.email)) {
        //     errors.email = 'Email is invalid';
        // }

        // if (!data.password) {
        //     errors.password = 'Password is required';
        // } else if (data.password.length < 8) {
        //     errors.password = `Password must be at 
        //     least 8 characters long`;
        // }

        // if (data.confirmPassword !== data.password) {
        //     errors.confirmPassword = 'Passwords do not match';
        // }

        return errors;
    };



    const handleChange = (e) => {
    //    let newData = []
        const { name, value } = e.target;
        // newData[name]= value
        // validateForm(newData)
        setFormData({
            ...formData,
            [name]: value,
        });
    };


	// Here usestate has been used in order
	// to set and get values from the jsx
	const [name, setName] = useState("");
    const [description, setDescription] = useState("");
	const [age, setAge] = useState("");
	const [id, setId] = useState("");
	// Used for navigation with logic in javascript
	let history = useNavigate();

	// Getting an index of an entry with an id
	// let index = array
	// 	.map(function (e) {
	// 		return e.id;
	// 	})
	// 	.indexOf(id);

        // Function for handling the edit and
	// pushing changes of editing/updating
	const handelSubmit = (e) => {
		// Preventing from reload
		e.preventDefault();
		if (name == "" || age == "") {
			alert("invalid input");
			return;
		}

		// Getting an index of an array
		let a = array[index];
		// Putting the value from the input
		// textfield and replacing it from
		// existing for updation
		a.Name = name;
		a.Age = age;
        a.Description = description;


		// Redirecting to main page
		history("/");
	};

	// Useeffect take care that page will
	// be rendered only once
	useEffect(() => {
		setName(localStorage.getItem("Name"));
        setDescription(localStorage.getItem("Description"));
		setAge(localStorage.getItem("Age"));
		setId(localStorage.getItem("id"));
	}, []);

	return (
<>
		<Container>
            <CommonForm 
                type={"edit"}
                handleSubmit={handleSubmit}
                errors={errors}
                handleChange={handleChange}
                formData={formData}
            />
        </Container>

		<div>
			<Form className="d-grid gap-2" style={{ margin: "5rem" }}>
				{/* setting a name from the input textfiled */}
				<Form.Group className="mb-3" controlId="formBasicName">
					<Form.Control
						value={name}
						onChange={(e) =>
							setName(e.target.value)
						}
						type="text"
						placeholder="Enter Name"
					/>
				</Form.Group>
                <Form.Group className="mb-3" controlId="formBasicDescription">
					<Form.Control
						value={description}
						onChange={(e) =>
							setDescription(e.target.value)
						}
						type="textarea"
						placeholder="Enter Description"
					/>
				</Form.Group>
				{/* setting a age from the input textfiled */}
				<Form.Group
					className="mb-3"
					controlId="formBasicPassword"
				>
					<Form.Control
						value={age}
						onChange={(e) =>
							setAge(e.target.value)
						}
						type="number"
						placeholder="Age"
					/>
				</Form.Group>

				{/* Hadinling an onclick event 
					running an edit logic */}
				<Button
					onClick={(e) => handelSubmit(e)}
					variant="primary"
					type="submit"
					size="lg"
				>
					Update
				</Button>

				{/* Redirecting to main page after editing */}
				<Link className="d-grid gap-2" to="/">
					<Button variant="warning" size="lg">
						Home
					</Button>
				</Link>
			</Form>
		</div>
		</>
	);
}

export default Edit;
